INSERT INTO tbl_faq VALUES("1","What is Tel?","<p>\n\n	<span style=\"color: rgb(51, 51, 51); font-family: Roboto; font-size: 15px; line-height: 28px; text-align: justify;\">The&nbsp;</span><strong style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: Roboto; font-size: 15px; line-height: 28px; text-align: justify;\">Technology-enhanced Learning (TeL) Centre</strong><span style=\"color: rgb(51, 51, 51); font-family: Roboto; font-size: 15px; line-height: 28px; text-align: justify;\">&nbsp;is an innovative Public-Private Partnership initiative between Imo State Polytechnic and NetPro International Nigeria Limited.</span></p>\n\n","1","Active");
